import React from 'react';
import ScrollAnimation from 'react-animate-on-scroll';
import ScrollableAnchor from 'react-scrollable-anchor';

import Project from '../../components/Project';
import { Box, Grid, Typography } from '@material-ui/core';

import karbonara from '../../../data/karbonara-artboard.jpg';
import microbitcoin from '../../../data/microbitcoin-artboard.jpg';
import aok from '../../../data/aok-artboard.png';
import harry from '../../../data/harry-artboard.jpg';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(() => ({
    subtitle: {
        width: '75%',
        marginBottom: '70px',
    },
}));

const ProjectsList = () => {
    const classes = useStyles();

    const defaultProjects = [
        {
            poster: karbonara,
            title: 'Karbonara – Online Marketplace',
            description: 'Online marketplace, where you can sell goods and services using cryptocurrency called Karbo.',
        },
        {
            poster: aok,
            title: 'AOK - Blockchain-based Platform',
            description:
                'Secure Proof-of-Stake Blockchain technology that reorganizes the world distribution industry.',
        },
        {
            poster: microbitcoin,
            title: 'MicroBitcoin – Payment Protocol',
            description:
                'Payments protocol built on blockchain technology, with decentralization and peer-to-peer payments.',
        },
        {
            poster: harry,
            title: 'Harry Bot - Speech-to-Text Bot',
            description:
                'Bot translates the voice messages into text in popular messengers. Participator of Y Combinator Startup School Batch.',
        },
    ];

    return (
        <ScrollableAnchor id={'fields'}>
            <Box my={12}>
                <ScrollAnimation animateIn="fadeIn" animateOnce={true} delay={200}>
                    <Grid container spacing={2} align="center">
                        <Grid item md={12}>
                            <Typography variant="h3" align="center" component="h2" gutterBottom>
                                Our Projects
                            </Typography>
                            <Typography variant="subtitle1" component="p" className={classes.subtitle}>
                                Above all, we are engineers, and our mission is to find the most elegant and economical
                                solutions to business problems.
                            </Typography>
                        </Grid>
                    </Grid>
                    <Grid container spacing={4} alignItems="center" direction="row">
                        {defaultProjects.map((project, index) => {
                            return (
                                <Grid key={index} item xs={12} md={6}>
                                    <Project data={project} />
                                </Grid>
                            );
                        })}
                    </Grid>
                </ScrollAnimation>
            </Box>
        </ScrollableAnchor>
    );
};

export default ProjectsList;
