        },
    ],
    [],
];

const Application = () => {
    return (
        <BrowserRouter>
            <ThemeProvider theme={theme}>
                <CssBaseline />
                <Nav />
                <Switch>
                    <Route path="/" render={() => <Home />} exact />
                    <Route
                        path="/web"
                        render={() => (
                            <Field
                                projects={projects}
                                headerInfo={headersInfo[0]}
                                technologies={technologies}
                                techDesc={techDesc[0]}
                                techList={techList[0]}
                            />
                        )}
                        exact
                    />
                    <Route
                        path="/mobile"
                        render={() => (
                            <Field
                                projects={projects}
                                headerInfo={headersInfo[1]}
                                technologies={technologies}
