import React from 'react';
import ScrollAnimation from 'react-animate-on-scroll';
import ScrollableAnchor from 'react-scrollable-anchor';
import Link from '@material-ui/core/Link';
import Tooltip from '@material-ui/core/Tooltip';

import { makeStyles } from '@material-ui/core/styles';

import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
// eslint-disable-next-line
import Box from '@material-ui/core/Box';

import react from '../../../data/react.svg';
import redux from '../../../data/redux.svg';
import bitcoin from '../../../data/bitcoin.svg';
import laravel from '../../../data/laravel.svg';
import flask from '../../../data/flask.svg';

const useStyles = makeStyles((theme) => ({
    shape: {
        zIndex: '-1',
        [theme.breakpoints.down('md')]: {
            opacity: '0.4',
        },
    },
}));

const Technologies = () => {
    const classes = useStyles();

    return (
        <ScrollableAnchor id={'technologies'}>
            <Box my={12} display="flex" alignItems="center" flexDirection="column">
                <Typography variant="h3" align="center" component="h2" gutterBottom>
                    The technologies that we use
                </Typography>
                <Typography variant="subtitle1" align="center" component="p" style={{ width: '75%' }} gutterBottom>
                    Above all, we are engineers, and our mission is to find the most elegant and economical solutions
                    for business problems. In our work we use various tools, technologies, and programming languages,
                    the main of them listed below.
                </Typography>
                <Grid container spacing={4} style={{ marginTop: 10 }} direction="row">
                   <Grid item container direction="column" sm={4}>
                        <Grid item container direction="row">
                            <Grid item xs={4} md={2}>
                                <ScrollAnimation animateIn="fadeIn" animateOnce={true}>
                                    <Link href="https://reactnative.dev/" target="_blank">
                                        <Tooltip title="React" placement="top">
                                            <img alt="react" src={react} height="70" />
                                        </Tooltip>
                                    </Link>
                                </ScrollAnimation>
                            </Grid>
                            <Grid item xs={4} md={2}>
                                <ScrollAnimation animateIn="fadeIn" animateOnce={true} delay={200}>
                                    <Link href="https://redux.js.org/" target="_blank">
                                        <Tooltip title="Redux" placement="top">
                                            <img alt="redux" src={redux} height="60" />
                                        </Tooltip>
                                    </Link>
                                </ScrollAnimation>
                            </Grid>
                        </Grid>
                       <Grid item>
                           <Typography variant="h5" align="center" component="h3" gutterBottom>
                               Solid Design
                           </Typography>
                           <Typography variant="subtitle2" align="center" component="p">
                               We create a timeless design, it doesn't get out of date after one-week usage
                           </Typography>
                       </Grid>
                   </Grid>
                </Grid>
            </Box>
        </ScrollableAnchor>
    );
};

export default Technologies;
