import React from 'react';
import { BrowserRouter, Route, Switch } from 'react-router-dom';
import { ErrorComponent, Home, Mobile } from '../pages';
import CssBaseline from '@material-ui/core/CssBaseline';
import { ThemeProvider } from '@material-ui/core/styles';
import theme from './theme';
import { Footer, Nav } from '../components';
import karbonara from '../../data/karbonara-artboard.jpg';
import aok from '../../data/aok-artboard.png';
import microbitcoin from '../../data/microbitcoin-artboard.jpg';
import harry from '../../data/harry-artboard.jpg';
import mobile from '../../data/findMoreIcons/mobile.png';
import react from '../../data/react.svg';
import redux from '../../data/redux.svg';
import java from '../../data/java.png';
import swift from '../../data/swift.png';
import kotlin from '../../data/kotlin.png';

const projects = [
    {
        poster: karbonara,
        title: 'Karbonara – Online Marketplace',
        description: 'Online marketplace, where you can sell goods and services using cryptocurrency called Karbo.',
    },
    {
        poster: aok,
        title: 'AOK - Blockchain-based Platform',
        description: 'Secure Proof-of-Stake Blockchain technology that reorganizes the world distribution industry.',
    },
    {
        poster: microbitcoin,
        title: 'MicroBitcoin – Payment Protocol',
        description:
            'Payments protocol built on blockchain technology, with decentralization and peer-to-peer payments.',
    },
    {
        poster: harry,
        title: 'Harry Bot - Speech-to-Text Bot',
        description:
            'Bot translates the voice messages into text in popular messengers. Participator of Y Combinator Startup School Batch.',
    },
];

const headersInfo = [
    {
        icon: 'icon',
        title: 'Web',
        description: 'We are',
    },
    {
        icon: mobile,
        title: 'Mobile',
        description:
            'Above all, we are engineers, and our mission is to find the most elegant and economical solutions to business problems. In our work we use various tools,',
    },
    {
        icon: 'icon',
        title: 'Blockchain',
        description: 'We are',
    },
    {
        icon: 'icon',
        title: 'AI',
        description: 'We are',
    },
];

const technologies = [
    {
        title: 'Cross-platform',
        desc: 'Above all, we are engineers, and our mission is to find the most elegant and economical solutions to business problems.',
        icons: [
            {
                title: 'React',
                icon: react,
                url: 'https://reactnative.dev/',
            },
            {
                title: 'Redux',
                icon: redux,
                url: 'https://redux.js.org/',
            },
        ],
    },
    {
        title: 'Native',
        desc: 'Above all, we are engineers, and our mission is to find the most elegant and economical solutions to business problems.',
        icons: [
            {
                title: 'Java',
                icon: java,
                url: 'https://www.java.com/',
            },
            {
                title: 'Swift',
                icon: swift,
                url: 'https://developer.apple.com/swift/',
            },
            {
                title: 'Kotlin',
                icon: kotlin,
                url: 'https://kotlinlang.org//',
            },
        ],
    },
];

const techDesc =
    'Above all, we are engineers, and our mission is to find the most elegant and economical solutions to business problems. In our work we use various tools, technologies and programming languages, but most often it is JavaScript.';

const techList = [
    {
        id: '#01',
        title: 'The technologies that we use',
        desc: 'Above all, we are engineers, and our mission is to find the most elegant and economical solutions to business problems.',
    },
    {
        id: '#02',
        title: 'The technologies that we use',
        desc: 'Above all, we are engineers, and our mission is to find the most elegant and economical solutions to business problems.',
    },
    {
        id: '#03',
        title: 'The technologies that we use',
        desc: 'Above all, we are engineers, and our mission is to find the most elegant and economical solutions to business problems.',
    },
];

const Application = () => {
    return (
        <BrowserRouter>
            <ThemeProvider theme={theme}>
                <CssBaseline />
                <Nav />
                <Switch>
                    <Route path="/" render={(data) => <Home />} exact />
                    <Route
                        path="/mobile"
                        render={
                            <Mobile
                                projects={projects}
                                headerInfo={headersInfo}
                                technologies={technologies}
                                techDesc={techDesc}
                                techList={techList}
                            />
                        }
                        exact
                    />
                    <Route component={ErrorComponent} />
                </Switch>
                <Footer />
            </ThemeProvider>
        </BrowserRouter>
    );
};

Application.propTypes = {};

export default Application;
