import React, { useEffect } from 'react';
import { configureAnchors } from 'react-scrollable-anchor';
import { ParallaxProvider } from 'react-scroll-parallax';
import Container from '@material-ui/core/Container';
import Header from './Header';
import Technologies from './Technologies';
import TechList from './TechList';
import ProjectsList from './ProjectsList';
import { ContactUs } from '../../components';
import karbonara from '../../../data/karbonara-artboard.jpg';
import aok from '../../../data/aok-artboard.png';
import microbitcoin from '../../../data/microbitcoin-artboard.jpg';
import harry from '../../../data/harry-artboard.jpg';

import mobile from '../../../data/findMoreIcons/mobile.png';

const Mobile = () => {
    const projects = [
        {
            poster: karbonara,
            title: 'Karbonara – Online Marketplace',
            description: 'Online marketplace, where you can sell goods and services using cryptocurrency called Karbo.',
        },
        {
            poster: aok,
            title: 'AOK - Blockchain-based Platform',
            description:
                'Secure Proof-of-Stake Blockchain technology that reorganizes the world distribution industry.',
        },
        {
            poster: microbitcoin,
            title: 'MicroBitcoin – Payment Protocol',
            description:
                'Payments protocol built on blockchain technology, with decentralization and peer-to-peer payments.',
        },
        {
            poster: harry,
            title: 'Harry Bot - Speech-to-Text Bot',
            description:
                'Bot translates the voice messages into text in popular messengers. Participator of Y Combinator Startup School Batch.',
        },
    ];

    const headersInfo = [
        {
            icon: 'icon',
            title: 'Web',
            description: 'We are',
        },
        {
            icon: mobile,
            title: 'Mobile',
            description:
                'Above all, we are engineers, and our mission is to find the most elegant and economical solutions to business problems. In our work we use various tools,',
        },
        {
            icon: 'icon',
            title: 'Blockchain',
            description: 'We are',
        },
        {
            icon: 'icon',
            title: 'AI',
            description: 'We are',
        },
    ];

    useEffect(() => {
        configureAnchors({ offset: -60, scrollDuration: 200 });
    });

    return (
        <ParallaxProvider>
            <Container>
                <Header headerInfo={headersInfo[1]} />
                <Technologies />
                <TechList />
                <ProjectsList projects={projects} />
                <ContactUs />
            </Container>
        </ParallaxProvider>
    );
};

export { Mobile };
