import React, { useState } from 'react';
import ScrollableAnchor from 'react-scrollable-anchor';
import { Box, Grid, Hidden, Typography } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

import arrowRight from '../../../data/arrow-down-right.png';
import arrowLeft from '../../../data/arrow-down-left.png';
import { Parallax } from 'react-scroll-parallax';
import right from '../../../data/backs/3.svg';
import left from '../../../data/backs/4.svg';

const useStyles = makeStyles((theme) => ({
    listNumber: {
        color: theme.palette.primary.transparent.light,
        fontWeight: 'lighter',
        paddingLeft: '27px',
    },
    subtitle: {
        width: '75%',
    },
    arrow: {
        height: '50%',
        width: '50%',
    },
    evenListItem: {
        marginTop: '110px',
        marginBottom: '110px',
    },
    shape: {
        zIndex: '-1',
        [theme.breakpoints.down('xs')]: {
            visibility: 'hidden',
        },
        [theme.breakpoints.down('md')]: {
            opacity: '0.4',
        },
    },
}));

const TechList = ({ techList }) => {
    const classes = useStyles();
    const [isLastEvenItem, setIsLastEvenItem] = useState(false);
    const rightStyle = {
        position: 'absolute',
        right: 0,
    };

    const leftStyle = {
        position: 'absolute',
        left: 0,
    };
    for (var i = 0; i < techList.length; i += 2) {
        console.log('grgrgr');
    }
    const listLength = techList.length;
    console.log(listLength);

    return (
        <ScrollableAnchor id={'technologies'}>
            <Grid container alignItems="center" spacing={3}>
                <Parallax y={[100, 0]} styleInner={leftStyle}>
                    <img alt="second" src={left} className={classes.shape} style={{ transform: 'scale(-1, -1)' }} />
                </Parallax>
                <Parallax y={[100, 0]} styleInner={rightStyle}>
                    <img alt="second" src={right} className={classes.shape} style={{ transform: 'scale(-1, 1)' }} />
                </Parallax>

                {techList.map((tech, index) => {
                    if (index % 2) {
                        //Last listItem
                        if (index === listLength - 1) {
                            setIsLastEvenItem(true);
                        }
                        //ListItem with arrows
                        return (
                            <>
                                <Hidden smDown>
                                    <Grid container item direction="row" xs={11} justify="flex-end" key={index}>
                                        <Grid item md={2}>
                                            <Box display="flex" flexDirection="column" alignItems="flex-end">
                                                <img
                                                    alt="arrow"
                                                    src={arrowRight}
                                                    style={{ paddingBottom: '40px' }}
                                                    className={classes.arrow}
                                                />
                                                <img
                                                    alt="arrow"
                                                    src={arrowLeft}
                                                    className={classes.arrow}
                                                    hidden={index === listLength - 1}
                                                />
                                            </Box>
                                        </Grid>
                                        <Grid item md={2}>
                                            <Typography
                                                variant="h2"
                                                align="left"
                                                color="textSecondary"
                                                className={`${classes.listNumber} ${classes.evenListItem}`}
                                            >
                                                {tech.id}
                                            </Typography>
                                        </Grid>
                                        <Grid item xs={9} md={7} className={classes.evenListItem}>
                                            <Typography variant="h3" component="h2" gutterBottom>
                                                {tech.title}
                                            </Typography>
                                            <Typography
                                                variant="subtitle1"
                                                component="p"
                                                gutterBottom
                                                className={classes.subtitle}
                                            >
                                                {tech.desc}
                                            </Typography>
                                        </Grid>
                                    </Grid>
                                </Hidden>
                                <Hidden mdUp>
                                    <Grid
                                        container
                                        item
                                        direction="row"
                                        xs={11}
                                        justify="flex-end"
                                        alignItems="center"
                                        key={index}
                                    >
                                        <Grid item xs={3}>
                                            <Typography
                                                variant="h2"
                                                align="center"
                                                color="textSecondary"
                                                className={classes.listNumber}
                                            >
                                                {tech.id}
                                            </Typography>
                                        </Grid>
                                        <Grid item xs={9} md={7}>
                                            <Typography variant="h3" component="h2" gutterBottom>
                                                {tech.title}
                                            </Typography>
                                            <Typography
                                                variant="subtitle1"
                                                component="p"
                                                gutterBottom
                                                className={classes.subtitle}
                                            >
                                                {tech.desc}
                                            </Typography>
                                        </Grid>
                                    </Grid>
                                </Hidden>
                            </>
                        );
                    }
                    //ListItem without arrows
                    return (
                        <Grid key={index} container item direction="row" xs={11} alignItems="center">
                            <Grid item xs={3}>
                                <Typography
                                    variant="h2"
                                    align="center"
                                    color="textSecondary"
                                    className={classes.listNumber}
                                >
                                    {tech.id}
                                </Typography>
                            </Grid>
                            <Grid item xs={9}>
                                <Typography variant="h3" component="h2" gutterBottom>
                                    {tech.title}
                                </Typography>
                                <Typography variant="subtitle1" component="p" className={classes.subtitle} gutterBottom>
                                    {tech.desc}
                                </Typography>
                            </Grid>
                        </Grid>
                    );
                })}
            </Grid>
        </ScrollableAnchor>
    );
};

export default TechList;
